import React, { useEffect, useState } from 'react';

const API_BASE = "http://localhost:7071/api";

// Mock data
const MOCK_SERVER_DATA = [
  { host: '10.2.88.10', hostname: 'WAS-APP-01', status: 'Running', version: '9.0.5.12' },
  { host: '10.2.88.11', hostname: 'WAS-APP-02', status: 'Running', version: '9.0.5.12' },
  { host: '10.2.88.12', hostname: 'WAS-APP-03', status: 'Stopped', version: '9.0.5.12' },
  { host: '10.2.88.13', hostname: 'WAS-APP-04', status: 'Running', version: '9.0.5.12' }
];

const MOCK_APP_DATA = [
  { application: 'CustomerPortal', server: 'WAS-APP-01', status: 'Started', instances: 4 },
  { application: 'PaymentService', server: 'WAS-APP-02', status: 'Started', instances: 2 },
  { application: 'ReportingApp', server: 'WAS-APP-03', status: 'Stopped', instances: 0 },
  { application: 'NotificationService', server: 'WAS-APP-04', status: 'Started', instances: 3 },
  { application: 'DataSync', server: 'WAS-APP-01', status: 'Started', instances: 2 }
];

const MOCK_JVM_DATA = [
  { server: 'WAS-APP-01', heapUsed: '2.4 GB', heapMax: '4.0 GB', usage: '60%' },
  { server: 'WAS-APP-02', heapUsed: '1.8 GB', heapMax: '4.0 GB', usage: '45%' },
  { server: 'WAS-APP-03', heapUsed: '0.5 GB', heapMax: '4.0 GB', usage: '12%' },
  { server: 'WAS-APP-04', heapUsed: '3.2 GB', heapMax: '4.0 GB', usage: '80%' }
];

const MOCK_THREAD_DATA = [
  { server: 'WAS-APP-01', active: 45, max: 100, percentage: '45%' },
  { server: 'WAS-APP-02', active: 32, max: 100, percentage: '32%' },
  { server: 'WAS-APP-03', active: 5, max: 100, percentage: '5%' },
  { server: 'WAS-APP-04', active: 78, max: 100, percentage: '78%' }
];

const badge = (status) => {
  const s = status.toLowerCase();
  if (s.includes('running') || s.includes('started')) {
    return <span className="px-3 py-1 rounded-lg bg-green-500 text-white font-bold text-sm">{status}</span>;
  }
  return <span className="px-3 py-1 rounded-lg bg-red-500 text-white font-bold text-sm">{status}</span>;
};

const WasDashboard = () => {
  const [serverData, setServerData] = useState([]);
  const [appData, setAppData] = useState([]);
  const [jvmData, setJvmData] = useState([]);
  const [threadData, setThreadData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAll();
  }, []);

  const loadAll = async () => {
    setLoading(true);

    try {
      const [srv, app, jvm, thread] = await Promise.allSettled([
        fetch(`${API_BASE}/was-server-status`).then(r => r.ok ? r.json() : Promise.reject()),
        fetch(`${API_BASE}/was-app-status`).then(r => r.ok ? r.json() : Promise.reject()),
        fetch(`${API_BASE}/was-jvm-status`).then(r => r.ok ? r.json() : Promise.reject()),
        fetch(`${API_BASE}/was-thread-status`).then(r => r.ok ? r.json() : Promise.reject())
      ]);

      setServerData(srv.status === 'fulfilled' && srv.value.length > 0 ? srv.value : MOCK_SERVER_DATA);
      setAppData(app.status === 'fulfilled' && app.value.length > 0 ? app.value : MOCK_APP_DATA);
      setJvmData(jvm.status === 'fulfilled' && jvm.value.length > 0 ? jvm.value : MOCK_JVM_DATA);
      setThreadData(thread.status === 'fulfilled' && thread.value.length > 0 ? thread.value : MOCK_THREAD_DATA);
    } catch (err) {
      setServerData(MOCK_SERVER_DATA);
      setAppData(MOCK_APP_DATA);
      setJvmData(MOCK_JVM_DATA);
      setThreadData(MOCK_THREAD_DATA);
    }

    setLoading(false);
  };

  return (
    <div className="grid grid-cols-2 gap-6">
      {/* Left Column */}
      <div className="space-y-6">
        {/* Server Status */}
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-bold text-gray-800 mb-4">WAS Server Status</h3>
          <table className="w-full">
            <thead>
              <tr className="border-b">
                <th className="text-left py-2 px-4 text-gray-600">Host</th>
                <th className="text-left py-2 px-4 text-gray-600">Hostname</th>
                <th className="text-left py-2 px-4 text-gray-600">Status</th>
              </tr>
            </thead>
            <tbody>
              {serverData.map((item, i) => (
                <tr key={i} className="border-b hover:bg-gray-50">
                  <td className="py-3 px-4">{item.host}</td>
                  <td className="py-3 px-4">{item.hostname}</td>
                  <td className="py-3 px-4">{badge(item.status)}</td>
                </tr>
              ))}
            </tbody>
          </table>
          {loading && <p className="text-gray-500 text-sm mt-2">Loading...</p>}
        </div>

        {/* Application Status */}
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-bold text-gray-800 mb-4">Application Status</h3>
          <table className="w-full">
            <thead>
              <tr className="border-b">
                <th className="text-left py-2 px-4 text-gray-600">Application</th>
                <th className="text-left py-2 px-4 text-gray-600">Server</th>
                <th className="text-left py-2 px-4 text-gray-600">Status</th>
              </tr>
            </thead>
            <tbody>
              {appData.map((item, i) => (
                <tr key={i} className="border-b hover:bg-gray-50">
                  <td className="py-3 px-4">{item.application}</td>
                  <td className="py-3 px-4">{item.server}</td>
                  <td className="py-3 px-4">{badge(item.status)}</td>
                </tr>
              ))}
            </tbody>
          </table>
          {loading && <p className="text-gray-500 text-sm mt-2">Loading...</p>}
        </div>
      </div>

      {/* Right Column */}
      <div className="space-y-6">
        {/* JVM Heap Usage */}
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-bold text-gray-800 mb-4">JVM Heap Usage</h3>
          <div className="space-y-4">
            {jvmData.map((item, i) => {
              const pct = parseInt(item.usage) || 0;
              const color = pct >= 80 ? '#ef4444' : pct >= 60 ? '#f59e0b' : '#10b981';

              return (
                <div key={i} className="border rounded-lg p-4">
                  <div className="font-bold text-blue-600 mb-2">{item.server}</div>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm text-gray-600">Heap: {item.heapUsed} / {item.heapMax}</span>
                    <span className="text-sm font-bold" style={{ color }}>{item.usage}</span>
                  </div>
                  <div className="w-full h-3 bg-gray-200 rounded-full overflow-hidden">
                    <div style={{ width: item.usage, backgroundColor: color }} className="h-full rounded-full"></div>
                  </div>
                </div>
              );
            })}
          </div>
          {loading && <p className="text-gray-500 text-sm mt-2">Loading...</p>}
        </div>

        {/* Thread Pool Status */}
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-bold text-gray-800 mb-4">Thread Pool Status</h3>
          <div className="space-y-4">
            {threadData.map((item, i) => {
              const pct = parseInt(item.percentage) || 0;
              const color = pct >= 80 ? '#ef4444' : pct >= 60 ? '#f59e0b' : '#10b981';

              return (
                <div key={i} className="border rounded-lg p-4">
                  <div className="font-bold text-blue-600 mb-2">{item.server}</div>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm text-gray-600">Active: {item.active} / {item.max}</span>
                    <span className="text-sm font-bold" style={{ color }}>{item.percentage}</span>
                  </div>
                  <div className="w-full h-3 bg-gray-200 rounded-full overflow-hidden">
                    <div style={{ width: item.percentage, backgroundColor: color }} className="h-full rounded-full"></div>
                  </div>
                </div>
              );
            })}
          </div>
          {loading && <p className="text-gray-500 text-sm mt-2">Loading...</p>}
        </div>
      </div>
    </div>
  );
};

export default WasDashboard;
