import React, { useState } from 'react';

const menuItems = [
  'Home',
  'ACE Dashboard',
  'WAS Dashboard',
  'Health Checks',
  'E-Cat | HSBC',
  'Database',
  'Command Executor',
  'HUB01',
  'HUB03',
  'ACE Frontends',
  'Reports'
];

const Sidebar = () => {
  const [active, setActive] = useState('Home');

  return (
    <aside className="w-64 bg-slate-900 h-screen fixed left-0 top-[88px]">
      <div className="p-6">
        <h2 className="text-white text-xl font-bold mb-6">Control Center</h2>
        <nav className="space-y-2">
          {menuItems.map((item) => (
            <button
              key={item}
              onClick={() => setActive(item)}
              className={`w-full text-left px-4 py-3 rounded-lg transition-all ${
                active === item
                  ? 'bg-white text-slate-900 font-semibold'
                  : 'text-gray-300 hover:bg-slate-800 hover:text-white'
              }`}
            >
              {item}
            </button>
          ))}
        </nav>
      </div>
    </aside>
  );
};

export default Sidebar;
